<?phpfunction wpcs_custom_search_func(){$html = '<div class="custom_search" style="background-color:#'.get_option('wpcs_back_color').'">';$html .= '<p style="color:#'.get_option('wpcs_text_color').';font-size:'.get_option('wpcs_top_head_px').'px;">'.get_option('wpcs_top_head').'</p>';$html .= '<p style="color:#'.get_option('wpcs_text_color').';font-size:'.get_option('wpcs_sub_head_px').'px;">'.get_option('wpcs_sub_head').'</p>';    $html .=  '<div style="clear:both;padding:10px;"></div>';$html .= '<div class="searchform-popup">            <form action="'.home_url().'" method="get" class="searchform ">        <fieldset>            <span class="text"><i class="fa fa-search"></i><input name="s" id="s" type="text" class="serachbox" value="" placeholder="'.get_option('wpcs_search_field').'" autocomplete="off"></span>                        <span class="button-wrap"><button class="btn btn-special" style="background-color:#'.get_option('wpcs_button_color').';color:#'.get_option('wpcs_text_color').'" title="'.get_option('wpcs_search_button').'" type="submit">'.get_option('wpcs_search_button').'</button></span>        </fieldset>    </form>        </div>';$html .=  '<div style="clear:both;padding:20px;"></div>'; $html .= '<ul class="custom_search_cat">';$hide_empty = get_option('wpcs_hide_empty');if($hide_empty == 1){	$hide = true;}else{	$hide = false;}if(get_option('wpcs_default_cat') == 1){$terms = get_terms( 'category', array('hide_empty' => $hide) );     foreach ( $terms as $term ) {	if( (get_option('wpcs_hide_uncat') == 1) && ($term->term_id == 1) ){}else{       $html .= '<li>'.'<a href="' . get_term_link( $term ) . '" style="background-color:#'.get_option('wpcs_cat_color').';color:#'.get_option('wpcs_text_color').'">' . $term->name . '</a></li>';	}             }}if(get_option('wpcs_custom_taxonomy') != NULL){	$custom_taxonomy = get_option('wpcs_custom_taxonomy');	$custom_tax_count = count( $custom_taxonomy );	if($custom_tax_count > 0){		for($i=0; $i<$custom_tax_count; $i++){		$terms = get_terms( $custom_taxonomy[$i], array('hide_empty' => $hide) );		     foreach ( $terms as $term ) {		       $html .= '<li>'.'<a href="' . get_term_link( $term ) . '" style="background-color:#'.get_option('wpcs_cat_color').';color:#'.get_option('wpcs_text_color').'">' . $term->name . '</a></li>';		     }		}	}}if(get_option('wpcs_wp_tags') == 1){	$tags = get_tags();	foreach ( $tags as $tag ) {		$tag_link = get_tag_link( $tag->term_id );					$html .= "<li><a href='{$tag_link}' title='{$tag->name} Tag' class='{$tag->slug}'  style='background-color:#".get_option('wpcs_cat_color').";color:#".get_option('wpcs_text_color')."'>";		$html .= "{$tag->name}</a></li>";	}}$html .= '</ul>';$html .= '<style>'.get_option('wpcs_custom_css').'</style>';return $html;}add_shortcode( 'wpcs_search', 'wpcs_custom_search_func' );?>